import {React,useState} from 'react';
import './App.css';
import Complete from './components/Complete';
import AddItem from './components/AddItem';
import Footer from './components/Footer';
import ViewItem from './components/ViewItem';
import Active from './components/Active';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";


  const  App=()=> {
  const [toDoLists,setToDoLists]=useState([]);
  const activeElement=[...toDoLists].filter(todo=>todo.isComplete===true);
  const active=[...toDoLists].filter(todo=>todo.isActive===true);
  console.log(activeElement.length);

  const clearCompleted=(id)=>
  {
    const updateToDoItems=[...toDoLists].filter(todo=>todo.isComplete!==true);

    setToDoLists(updateToDoItems);
  }




  return (
    <>
    let data=locals
    <div className="App">
    <h1 className="heading1">todos</h1>
    <AddItem toDoLists={toDoLists} setToDoLists={setToDoLists} />
    <Router>
    <label className="item-left">{active.length} items left</label>
    <Link to="/all"><button className="btn1">All</button></Link>
    <Link to="/active"><button className="btn2">Active</button></Link>
    <Link to="/complete"><button className="btn3">Complete</button></Link>
    <button className="btn4" onClick={clearCompleted} style={{visibility:activeElement.length!==0?null:"hidden"}}>Clear Completed</button>
    <Link to="/"></Link>
         <Switch>
         <Route path="/all">
         <ViewItem  toDoLists={toDoLists} setToDoLists={setToDoLists} />
          </Route>
          <Route path="/active">
          <Active toDoLists={toDoLists} setToDoLists={setToDoLists} />
          </Route>
          <Route path="/complete">
          <Complete  toDoLists={toDoLists} setToDoLists={setToDoLists} />
          </Route>
          <Route path="/">
         <ViewItem toDoLists={toDoLists} setToDoLists={setToDoLists} />
          </Route>
         </Switch>
         </Router>
    </div>
    </>
  );
}

export default App;
