import {React,useState} from 'react'
import './ViewItem.css';

const ViewItem = ({toDoLists,setToDoLists,active,setActive,complete,setComplete}) => {

    const deleteItem=(id)=>
    {
        const filteredToDoLists= toDoLists.filter(item=>item.id!==id)
        setToDoLists(filteredToDoLists);
         const updateActive=(todo)=>
  {
    const removedActive=[...todos].filter(todo=>todo.isComplete!==true)
    setActive(removedActive);
    console.log(active);

  }
    }

    const handleCompleteTask=(id)=>
    {
        const newToDoLists=toDoLists.map(item=>item.id===id?
            {
                id:item.id,
                text:item.text,
                isComplete:!item.isComplete
            }:item
            )
            setToDoLists(newToDoLists);
    }






    return (
        <section className="todoapp">
        <div className="view-item">
            {
                toDoLists.map(item=>{
                    return (
                    <form key={item.id}>
                        <input type="radio"
                        onClick={()=>handleCompleteTask(item.id)}
                        />
                        <p style={{textDecoration:item.isComplete && "line-through"}}>{item.text}</p>
                        <div className="btn-hover">
                            <button onClick={()=>deleteItem(item.id)}>X</button>
                        </div>
                    </form>
                    )
                })
            }
        </div>
        </section>
    )
}

export default ViewItem;
