import {React,useState} from 'react';
import './App.css';
import Complete from './components/Complete';
import AddItem from './components/AddItem';
import Footer from './components/Footer';
import ViewItem from './components/ViewItem';
import Active from './components/Active';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";





const  App=()=> {
  const [toDoLists,setToDoLists]=useState([]);
  const [active,setActive]=useState([]);
  const [complete,setComplete]=useState([]);

  const updateActive=(todo)=>
  {
    const removedActive=[...toDoLists].filter(todo=>todo.isComplete!==true)
    setActive(removedActive);
    console.log(active);
    <ViewItem toDoLists={active} setToDoLists={setActive} 
    />

  }

const updateComplete=(todo)=>
{
  const completedActive=[...toDoLists].filter(todo=>todo.isComplete===true)
  setComplete(completedActive);
  console.log(complete);
}



  return (

    <div className="App">
    <h1 className="heading1">todos</h1>

    <AddItem toDoLists={toDoLists} setToDoLists={setToDoLists} />
    {/* <ViewItem toDoLists={toDoLists} setToDoLists={setToDoLists} 
    /> */}
    <Router>
    <Link to="/all"><button>All</button></Link>
    <Link to="/active"><button onClick={updateActive}>Active</button></Link>
    <Link to="/complete"><button onClick={updateComplete}>Complete</button></Link>
         <Switch>
         <Route path="/all">
         <ViewItem  toDoLists={toDoLists} setToDoLists={setToDoLists} />
          </Route>
          <Route path="/active">
          <Active active={active} complete={complete} setComplete={complete} setActive={setActive} toDoLists={toDoLists} setToDoLists={setToDoLists} />
          </Route>
          <Route path="/complete">
          <Complete complete={complete} setComplete={complete} toDoLists={toDoLists} setToDoLists={setToDoLists} />
          </Route>
         </Switch>
         </Router>
    </div>
  );
}

export default App;
