import {React,useState} from 'react';
import './App.css';
import Complete from './components/Complete';
import AddItem from './components/AddItem';
import Footer from './components/Footer';
import ViewItem from './components/ViewItem';
import Active from './components/Active';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";





const  App=()=> {
  const [toDoLists,setToDoLists]=useState([]);
  const [style,setStyle]=useState({visibility:"hidden"})

  return (

    <div className="App">
    <h1 className="heading1">todos</h1>

    <AddItem toDoLists={toDoLists} setToDoLists={setToDoLists} />
    <Router>
    <Link to="/all"><button>All</button></Link>
    <Link to="/active"><button>Active</button></Link>
    <Link to="/complete"><button>Complete</button></Link>
    <button style={style}>Clear Completed</button>
    <Link to="/"></Link>
         <Switch>
         <Route path="/all">
         <ViewItem  toDoLists={toDoLists} setToDoLists={setToDoLists} />
          </Route>
          <Route path="/active">
          <Active toDoLists={toDoLists} setToDoLists={setToDoLists} />
          </Route>
          <Route path="/complete">
          <Complete  toDoLists={toDoLists} setToDoLists={setToDoLists} />
          </Route>
          <Route path="/">
         <ViewItem toDoLists={toDoLists} setToDoLists={setToDoLists} />
          </Route>
         </Switch>
         </Router>
    </div>
  );
}

export default App;
