import {React,useState} from 'react'
import './ViewItem.css';

const ViewItem = ({toDoLists,setToDoLists}) => {
    const [stylebtn, setStylebtn] = useState({display:'block'});

    const deleteItem=(id)=>
    {
        const filteredToDoLists2= toDoLists.filter(item=>item.id!==id)
        setToDoLists(filteredToDoLists2);

    }
    const handleAbandonTask=(id)=>
    {
        const newToDoLists=toDoLists.map(item=>item.id===id?
            {
                id:item.id,
                text:item.text,
                isAbandon:!item.isAbandon
            }:item
            )
            setToDoLists(newToDoLists);
            console.log(toDoLists);
    }

    const handleCompleteTask=(id)=>
    {
        const filteredToDoLists2= toDoLists.filter(item=>item.id!==id)
        setToDoLists(filteredToDoLists2);
        // // console.log(toDoLists);
        // const newToDoLists=toDoLists.map(item=>item.id===id?
        //     {

        //         id:item.id,
        //         text:item.text,
        //         isComplete:!item.isComplete,
        //         isPending:item.isPending,
        //         isProgress:item.isProgress,
        //         isAbandon:item.isAbandon
        //     }
        //     :item,
        //     )

        //     console.log(newToDoLists);
        //     //setToDoLists(newToDoLists);
        //     console.log(toDoLists);
    }
    const handlePendingTask=(id)=>
    {
        const newToDoLists=toDoLists.map(item=>item.id===id?
            {
                id:item.id,
                text:item.text,
                isPending:!item.isPending
            }:item
            )
            setToDoLists(newToDoLists);
            console.log(toDoLists);
    }

    const handleProgressTask=(id)=>
    {
        const newToDoLists=toDoLists.map(item=>item.id===id?
            {
                id:item.id,
                text:item.text,
                isProgress:!item.isProgress
            }:item
            )
            setToDoLists(newToDoLists);
            console.log(toDoLists);
    }

    return (
        <div className="todoapp">
        <div className="view-item">
            {
                toDoLists.map(item=>{
                    return (

                    <div  className="wrapper-items">
                    <form className="view-form-items" key={item.id}>
                    <input type="radio"
                        className="input-btn"
                        onClick={()=>handleCompleteTask(item.id)}
                        />
                    <button style={stylebtn} onClick={()=>handleCompleteTask(item.id)}>Completed</button>
                    <p>{item.text}</p>
                    <button style={stylebtn} onClick={()=>handleAbandonTask(item.id)}>Abandon</button>
                    <button style={stylebtn} onClick={()=>handlePendingTask(item.id)}>Pending</button>
                    <button style={stylebtn} onClick={()=>handleProgressTask(item.id)}>InProgress</button>
                    <button style={stylebtn} onClick={()=>deleteItem(item.id)}>X</button>
                    </form>
                    </div>
                )
                })
            }
        </div>
        </div>
    )
}

export default ViewItem;
