import {React,useState} from 'react'
import './ViewItem.css';

const Abandon = ({toDoLists,setToDoLists}) => {
    const active=[...toDoLists].filter(todo=>todo.isActive===true)
    const [stylebtn, setStylebtn] = useState({display:'none'});


    const deleteItem=(id)=>
    {
        const filteredToDoLists2= toDoLists.filter(item=>item.id!==id)
        setToDoLists(filteredToDoLists2);
    }

    const handleCompleteTask=(id)=>
    {
        const newToDoLists=toDoLists.map(item=>item.id===id?
            {
                id:item.id,
                text:item.text,
                isComplete:!item.isComplete,
                isActive:!item.isActive
            }:item
            )
            setToDoLists(newToDoLists);
    }


    const handlePendingTask=(id)=>
    {
        const newToDoLists=toDoLists.map(item=>item.id===id?
            {
                id:item.id,
                text:item.text,
                isComplete:false,
                isPending:true,
                isProgress:false,
                isAbandon:false
            }:item
            )
            setToDoLists(newToDoLists);
            console.log(toDoLists);
    }

    return (
        <div className="todoapp">
        <div className="view-item">
            {
                toDoLists.map(item=>{
                    return (
                    <div  className="wrapper-items">
                    <form className="view-form-items" key={item.id}>
                    <p>{item.text}</p>
                    <input type="button"
                    value="Completed"
                    className="input-btn"
                    onClick={()=>handleCompleteTask(item.id)}
                    />
                    <input type="button"
                    value="Pending"
                    className="input-btn"
                    onClick={()=>handlePendingTask(item.id)}
                    />
                    {/* <input type="button"
                    value="Abandon"
                    className="input-btn"
                    onClick={()=>handleAbandonTask(item.id)}
                    /> */}
                    <input type="button"
                    value="InProgress"
                    className="input-btn"
                    onClick={()=>handleProgressTask(item.id)}
                    />
                    <button style={stylebtn} onClick={()=>deleteItem(item.id)}>X</button>
                    </form>
                    </div>
                )
                })
            }
        </div>
        </div>
    )
}

export default Abandon;
