import {React,useState} from 'react'
import './ViewItem.css';

const ViewItem = ({toDoLists,setToDoLists}) => {
    const [stylebtn, setStylebtn] = useState({display:'block'});

    const deleteItem=(id)=>
    {
        // const filteredToDoLists1= active.filter(item=>item.id!==id)
        // setActive(filteredToDoLists1);
        // const filteredToDoLists3= complete.filter(item=>item.id!==id)
        // setComplete(filteredToDoLists3);
        const filteredToDoLists2= toDoLists.filter(item=>item.id!==id)
        setToDoLists(filteredToDoLists2);

    }

    const handleCompleteTask=(id)=>
    {
        const newToDoLists=toDoLists.map(item=>item.id===id?
            {
                id:item.id,
                text:item.text,
                isComplete:!item.isComplete,
                isActive:!item.isActive
            }:item
            )
            setToDoLists(newToDoLists);
            console.log(toDoLists);
    }

    const mouseLeave=(e)=>
    {
        setStylebtn({display:"none"});

    }

    const mouseEnter=(e)=>
    {
        setStylebtn({display:"block"});

    }


    return (
        <div className="todoapp">
        <div className="view-item">
            {
                toDoLists.map(item=>{
                    return (
                    <form on className="view-form-items" key={item.id} onMouseLeave={mouseLeave} onMouseEnter={mouseEnter}>
                        <input type="radio" checked={item.isComplete===true}
                        onClick={()=>handleCompleteTask(item.id)}
                        />
                        <p style={{textDecoration:item.isComplete && "line-through"}}>{item.text}</p>

                            <button style={stylebtn} onClick={()=>deleteItem(item.id)}>X</button>
                    </form>
                    )
                })
            }
        </div>
        </div>
    )
}

export default ViewItem;
