import ViewItem from './components/ViewItem';

const  App=()=> {
  const [toDoLists,setToDoLists]=useState([]);
  const [active,setActive]=useState([]);
  const [complete,setComplete]=useState([]);

  const updateActive=(todo)=>
  {
    const removedActive=[...toDoLists].filter(todo=>todo.isComplete!==true)
    setActive(removedActive);
    console.log(active);

    <ViewItem toDoLists={active} setToDoLists={setActive} 
    />

  }

const updateComplete=(todo)=>
{
  const completedActive=[...toDoLists].filter(todo=>todo.isComplete===true)
  setComplete(completedActive);
  console.log(complete);
}



  return (
    <div className="App">
    <h1 className="heading1">todos</h1>
    <AddItem toDoLists={toDoLists} setToDoLists={setToDoLists} />
    <ViewItem toDoLists={toDoLists} setToDoLists={setToDoLists} 
    />
    <button onClick={updateActive} on>Active</button>
    <button onClick={updateComplete}>Complete</button>
    </div>
  );
}

export default App;
